import React, { useState } from "react";

const LoginForm = () => {

 let [isOpen, setIsOpen] = useState(false);

 let [loginIsOpen, setLoginIsOpen] = useState(false);

 function closeModal() {
   setIsOpen(false);
 }

 function openModal() {
   setIsOpen(true);
 }

 function openLoginModal() {
   setLoginIsOpen(true);
 }



 const [loginData, setLoginData] = useState({
   username: "",
   password: "",
 });

 const LoginHandleSubmit = async (event) => {
   event.preventDefault();
   setLoginData({
     username: "",
     password: "",
   });

   const url = "https://api.osbb.devserver.cc/authentication/login";

   const request = {
     clientId: "OSBB_CABINET_WEB",
     clientType: "WEB",
     deviceId: "3eb8f083-a18e-4ecd-a3b0-259553c75a5f",
     login: loginData.username,
     password: loginData.password,
   };
   console.log(request);

   closeModal();

   window.localStorage.setItem("login", loginData.username);
   window.localStorage.setItem("password", loginData.password);
 };



 const loginHandleChange = (event) => {
   const { name, value } = event.target;
   setLoginData((prevLoginData) => ({ ...prevLoginData, [name]: value }));
 };




  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [passwordError, setPasswordError] = useState("");

  const validatePhone = (value) => {
    const phonePattern = /^\+380\d{9}$/; 
    if (!phonePattern.test(value)) {
      setPhoneError("Please enter a valid Ukrainian phone number");
      return false;
    }
    setPhoneError("");
    return true;
  };

  const validatePassword = (value) => {
    if (value.length < 8) {
      setPasswordError("Password must be at least 8 characters long");
      return false;
    }
    setPasswordError("");
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validatePhone(phone) && validatePassword(password)) {
      console.log("Form submitted");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Phone Number:</label>
        <input
          type="text"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          onBlur={() => validatePhone(phone)}
        />
        {phoneError && <span>{phoneError}</span>}
      </div>
      <div>
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          onBlur={() => validatePassword(password)}
        />
        {passwordError && <span>{passwordError}</span>}
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default LoginForm;
