import React, { Fragment, useEffect, useState } from "react";
import { Dialog, Transition } from "@headlessui/react";
import api from "./api/posts";
import SLoginForm from "./components/SLoginForm";


function App() {


 function closeModal() {
   setIsOpen(false);
 }

 function openModal() {
   setIsOpen(true);
 }



  let [isOpen, setIsOpen] = useState(false);

  const [posts, setPosts] = useState([]);

  const [formData, setFormData] = useState({
    lat: "",
    lon: "",
    location: "",
    link: "",
  });




  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
  };



  const handleSubmit = async (event) => {
    event.preventDefault();
    setPosts((prev) => [formData, ...prev]);
    setFormData({
      lat: "",
      lon: "",
      location: "",
      link: "",
    });
    closeModal();
  };

  const deletePost = (index) => {
    setPosts((prev) => prev.filter((post, i) => i !== index));
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const responce = await api.get("/locations/units/map/simple");
        setPosts(responce.data);
      } catch (err) {
        console.log("Error fetching posts:", err);
      }
    };

    fetchPosts();
  }, []);

  return (
    <div>
      <h1 className="text-4xl font-bold text-center text-indigo-900">POSTS</h1>

     <SLoginForm />


      </div>
  );
}

export default App;
