import React, { useEffect, useState } from "react";
import { Dialog, Transition } from "@headlessui/react";
import api from "./api/posts";

function App() {


let [isOpen, setIsOpen] = useState(true);

function closeModal() {
  setIsOpen(false);
}

function openModal() {
  setIsOpen(true);
}


  const [posts, setPosts] = useState([]);

  const [formData, setFormData] = useState({
    lat: "",
    lon: "",
    location: "",
    link: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setPosts((prev) => [formData, ...prev]);
    setFormData({
      lat: "",
      lon: "",
      location: "",
      link: "",
    });
  };

  const deletePost = (index) => {
    setPosts((prev) => prev.filter((post, i) => i !== index));
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const responce = await api.get("/locations/units/map/simple");
        setPosts(responce.data);
      } catch (err) {
        console.log("Error fetching posts:", err);
      }
    };

    fetchPosts();
  }, []);



  return (
    <div>
      <h1 className="text-4xl font-bold text-center text-indigo-900">POSTS</h1>

      <div className="fixed inset-0 flex items-center justify-center">
        <button
          type="button"
          onClick={openModal}
          className="rounded-md bg-black bg-opacity-20 px-4 py-2 text-sm font-medium text-white hover:bg-opacity-30 focus:outline-none focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-opacity-75"
        >
          Open dialog
        </button>
      </div>

      <Transition appear show={isOpen} as={Fragment}>
        <Dialog as="div" className="relative z-10" onClose={closeModal}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-25" />
          </Transition.Child>

          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex min-h-full items-center justify-center p-4 text-center">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="w-full max-w-md transform overflow-hidden rounded-2xl bg-white p-6 text-left align-middle shadow-xl transition-all">
                  <Dialog.Title
                    as="h3"
                    className="text-lg font-medium leading-6 text-gray-900"
                  >
                    Payment successful
                  </Dialog.Title>
                  <div className="mt-2">
                    <p className="text-sm text-gray-500">
                      Your payment has been successfully submitted. We’ve sent
                      you an email with all of the details of your order.
                    </p>
                  </div>

                  <div className="mt-4">
                    <button
                      type="button"
                      className="inline-flex justify-center rounded-md border border-transparent bg-blue-100 px-4 py-2 text-sm font-medium text-blue-900 hover:bg-blue-200 focus:outline-none focus-visible:ring-2 focus-visible:ring-blue-500 focus-visible:ring-offset-2"
                      onClick={closeModal}
                    >
                      Got it, thanks!
                    </button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>





      <div className="w-full max-w-xs">
        <form
          onSubmit={handleSubmit}
          className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4"
        >
          <div className="mb-2">
            <label className="font-bold" htmlFor="lat">
              lat
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
              name="lat"
              id="lat"
              value={formData.lat}
              onChange={handleChange}
            />
          </div>
          <div className="mb-2">
            <label className="font-bold" htmlFor="lon">
              lon
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
              name="lon"
              id="lon"
              value={formData.lon}
              onChange={handleChange}
            />
          </div>
          <div className="mb-2">
            <label className="font-bold" htmlFor="location">
              location
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
              name="location"
              id="location"
              value={formData.location}
              onChange={handleChange}
            />
          </div>
          <div className="mb-2">
            <label className="font-bold" htmlFor="link">
              link
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
              name="link"
              id="link"
              value={formData.link}
              onChange={handleChange}
            />
          </div>

          <div className="flex items-center justify-between">
            <button
              onClick={handleSubmit}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              type="submit"
            >
              ADD +
            </button>
          </div>
        </form>
      </div>

      <div className="grid grid-cols-2 items-center">
        {posts.map((post, index) => (
          <div
            key={index}
            className="grid max-w-lg mb-5 p-6 bg-white border border-gray-200 
      rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700"
          >
            <p className="text-red-600 font-bold">lat: {post.lat}</p>
            <h3 className="text-indigo-300 font-bold">lon: {post.lon}</h3>
            <p className="text-indigo-600 font-bold">
              location: {post.location}
            </p>
            <p className="text-indigo-900 font-bold">link: {post.link}</p>
            <button
              onClick={() => deletePost(index)}
              className="bg-transparent hover:bg-red-500 text-red-700 font-semibold hover:text-white py-2 px-4 border border-red-500 hover:border-transparent rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
