import React, { useEffect, useState } from "react";
import api from "./api/posts";

function App() {
  const [posts, setPosts] = useState([]);

  const [formData, setFormData] = useState({
    Lat: "",
    Lon: "",
    Location: "",
    Link: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(
      `Lat: ${formData.message}, Lon: ${formData.message}, Location: ${formData.message}, Link: ${formData.message}`
    );
  };

  const deletePost = (index) => {
    setPosts((prev) => prev.filter((post, i) => i !== index));
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const responce = await api.get("/locations/units/map/simple");
        setPosts(responce.data);
      } catch (err) {
        if (err.responce) {
          console.log(err.responce.data);
          console.log(err.responce.status);
          console.log(err.responce.headers);
        } else {
          console.log(err.message);
        }
      }
    };

    fetchPosts();
  }, []);

  return (
    <div>
      <h1 className="text-4xl font-bold text-center text-indigo-900">POSTS</h1>
      <div class="w-full max-w-xs">
        <form class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
          <div class="mb-4">
            <label
              class="block text-gray-700 text-sm font-bold mb-2"
              for="username"
            >
              Username
            </label>
            <input
              class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              id="username"
              type="text"
              placeholder="Username"
            />
          </div>
          <div class="mb-6">
            <label
              class="block text-gray-700 text-sm font-bold mb-2"
              for="message"
            >
              Password
            </label>
            <input
              class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="password"
              type="text"
              placeholder="none"
            />
            <p class="text-red-500 text-xs italic">Please choose a password.</p>
          </div>
          <div class="flex items-center justify-between">
            <button
              class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              type="button"
            >
              Sign In
            </button>
            <a
              class="inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800"
              href="#"
            >
              Forgot Password?
            </a>
          </div>
        </form>
      </div>
      <div className="grid grid-cols-2 items-center">
        {posts.map((post, index) => (
          <div
            key={index}
            className="grid max-w-lg mb-5 p-6 bg-white border border-gray-200 
      rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700"
          >
            <p className="text-red-600 font-bold">Lat: {post.lat}</p>
            <h3 className="text-indigo-300 font-bold">Lon: {post.lon}</h3>
            <p className="text-indigo-600 font-bold">
              Location: {post.location}
            </p>
            <p className="text-indigo-900 font-bold">Link: {post.link}</p>
            <button
              onClick={() => deletePost(index)}
              class="bg-transparent hover:bg-red-500 text-red-700 font-semibold hover:text-white py-2 px-4 border border-red-500 hover:border-transparent rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
